SELECT \n    c.CampaignID,\n    s.Source_ID,\n    MAX(CASE WHEN con.Content_Row_ID = 'A' THEN con.Content_Value END) AS ContentRowID_A,\n    MAX(CASE WHEN con.Content_Row_ID = 'B' THEN con.Content_Value END) AS ContentRowID_B\nFROM \n    Campaign c\nJOIN \n    Source s ON c.CampaignID = s.Campaign_ID\nJOIN \n    Content con ON c.CampaignID = con.Campaign_ID\nGROUP BY \n    c.CampaignID, s.Source_ID;