-- Create a function to split the CSV into individual values\nCREATE FUNCTION dbo.Split(@String VARCHAR(4096), @Delimiter CHAR(1))\nRETURNS @Output TABLE(Value VARCHAR(255))\nAS\nBEGIN\n    DECLARE @Start INT, @End INT\n    SET @Start = 1\n    SET @End =CHARINDEX(@Delimiter,@String)\n\n    WHILE @Start < LEN(@String) + 1 BEGIN\n        IF @End = 0 \n            SET @End = LEN(@String) + 1\n\n        INSERT INTO @Output (Value)\n        VALUES(SUBSTRING(@String, @Start, @End - @Start))\n\n        SET @Start = @End + 1\n        SET @End = CHARINDEX(@Delimiter,@String,@Start+LEN(@Delimiter))\n    END\n\n    RETURN\nEND\nGO\n\n-- Create or modify your stored procedure to use the Split function\nCREATE PROCEDURE getSomething @keyList varchar(4096)\nAS\nBEGIN\n    SELECT * \n    FROM mytbl \n    WHERE name IN (SELECT Value FROM dbo.Split (@keyList, ','))\nEND