-- Step 1: Enable Full-Text Search (this step must be done by your DBA)\n-- EXEC sp_fulltext_service 'restart_all_fdhosts';\n\n-- Step 2: Create a Full-Text Catalog if it doesn't exist\nCREATE FULLTEXT CATALOG ArticlesCatalog;\n\n-- Step 3: Create a Full-Text Index on the Articles table\nCREATE FULLTEXT INDEX ON Articles(ArticleTitle, ArticleContent)\nKEY INDEX PK_Articles ON ArticlesCatalog -- PK_Articles is the primary key of your table\n\n-- Step 4: Write a Stored Procedure to find Similar Articles\nCREATE PROCEDURE [dbo].[FindSimilarArticles]\n    @searchText NVARCHAR(1024)\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    SELECT ArticleID, ArticleTitle, \n           (FREETEXTTABLE(Articles, *, @searchText) AS KEY_TBL).RANK AS RelevanceScore\n    FROM Articles INNER JOIN \n         FREETEXTTABLE(Articles, (ArticleTitle, ArticleContent), @searchText) \n                     AS KEY_TBL ON Articles.ArticleID = KEY_TBL.[KEY]\n    ORDER BY RelevanceScore DESC;\nEND;\n\n-- Example usage of the stored procedure\nEXEC FindSimilarArticles 'your search query here';