CREATE TABLE Fruits (\n    FruitID INT PRIMARY KEY,\n    Name VARCHAR(100) NOT NULL UNIQUE\n);\n\nCREATE TABLE Foo (\n    FooID INT PRIMARY KEY,\n    Value1 INT,\n    Value2 INT,\n    Value3 INT\n);\n\nCREATE TABLE FooFruit (\n    FooID INT,\n    FruitID INT,\n    PRIMARY KEY (FooID, FruitID),\n    FOREIGN KEY (FooID) REFERENCES Foo(FooID),\n    FOREIGN KEY (FruitID) REFERENCES Fruits(FruitID)\n);