-- Main log table with common fields\nCREATE TABLE log (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    user_id INT NOT NULL,\n    message VARCHAR(255) NOT NULL\n);\n\n-- Table for user-related logs\nCREATE TABLE log_user (\n    log_id INT PRIMARY KEY,\n    action ENUM('added', 'deleted', 'edited') NOT NULL,\n    reference_id INT NOT NULL,\n    FOREIGN KEY (log_id) REFERENCES log(id),\n    FOREIGN KEY (user_id) REFERENCES users(id)\n);\n\n-- Table for service-related logs\nCREATE TABLE log_service (\n    log_id INT PRIMARY KEY,\n    action ENUM('added', 'deleted', 'edited') NOT NULL,\n    reference_id INT NOT NULL,\n    FOREIGN KEY (log_id) REFERENCES log(id),\n    FOREIGN KEY (reference_id) REFERENCES services(id)\n);