WITH AllGrades AS (\n    SELECT 1 AS grade UNION ALL\n    SELECT 2 UNION ALL\n    SELECT 3 UNION ALL\n    SELECT 4 UNION ALL\n    SELECT 5 UNION ALL\n    SELECT 6\n)\nSELECT \n    ag.grade, \n    COUNT(g.grade) AS frequency\nFROM \n    AllGrades ag\nLEFT JOIN (\n    -- Simulate the rows of your grades table\n    SELECT 1 AS grade UNION ALL\n    SELECT 2 UNION ALL\n    SELECT 1 UNION ALL\n    SELECT 3 UNION ALL\n    SELECT 4 UNION ALL\n    SELECT 5\n) AS GradesTable g ON ag.grade = g.grade\nGROUP BY \n    ag.grade\nORDER BY \n    ag.grade;