-- Assume your original table is named 'OriginalTable'\n\n-- Step 1: Create a new table with the desired column order\nCREATE TABLE NewTable (\n    colA1 INT,\n    colA2 VARCHAR(50),\n    colA3 DATE,\n    colA4 BIT,\n    colA5 DECIMAL(18,2),\n    colA6 NVARCHAR(100),\n    colB1 TIME,\n    colB2 TIMESTAMP,\n    colB3 UNIQUEIDENTIFIER\n);\n\n-- Step 2: Insert data from the original table to the new one\nINSERT INTO NewTable (colA1, colA2, colA3, colA4, colA5, colA6, colB1, colB2, colB3)\nSELECT colA1, colA2, colA3, colA4, colA5, colA6, colB1, colB2, colB3\nFROM OriginalTable;\n\n-- Step 3: Drop the original table\nDROP TABLE OriginalTable;\n\n-- Step 4: Rename the new table to the old table name\nEXEC sp_rename 'NewTable', 'OriginalTable';