CREATE TRIGGER trg_UpdateFieldOnCondition\nON YourTableName\nAFTER UPDATE\nAS\nBEGIN\n    IF UPDATE(EvilERPProcessColumn)\n    BEGIN\n        UPDATE t\n        SET FieldToUpdate = NEW_VALUE -- Define what you want to set the field to\n        FROM YourTableName t\n        INNER JOIN inserted i ON t.PrimaryKeyColumn = i.PrimaryKeyColumn\n        WHERE i.EvilERPProcessColumn <> t.EvilERPProcessColumn; -- Optional condition if needed\n    END\nEND;