USE msdb;\nGO\n\nEXEC sp_add_maintenance_plan_job \n    @plan_name = 'MyMaintenancePlan',\n    @job_template = '(DESCRIPTION="Job template for log backup.")';\n\nDECLARE @plan_id UNIQUEIDENTIFIER;\nSELECT @plan_id = plan_id\nFROM msdb.dbo.sysmaintplan_plans\nWHERE (name = N'MyMaintenancePlan');\n\nEXEC sp_add_job \n    @job_name = 'TransactionLogBackup_Job',\n    @enabled = 1;\n\nDECLARE @job_id UNIQUEIDENTIFIER;\nSELECT @job_id = job_id\nFROM msdb.dbo.sysjobs\nWHERE (name = N'TransactionLogBackup_Job');\n\nEXEC sp_add_maintenance_plan_subplan \n    @plan_id = @plan_id,\n    @subplan_name = 'MySubPlan',\n    @description = 'Transaction log backup sub plan',\n    @job_id = @job_id;\n\nEXEC sp_add_schedule\n    @schedule_name = N'MySchedule',\n    @freq_type=4, -- Daily\n    @freq_interval=1, \n    @freq_subday_type=2, -- Minutes\n    @freq_subday_interval=15; -- Every 15 minutes\n\nDECLARE @sched_id INT;\nSELECT @sched_id = schedule_id\nFROM msdb..sysschedules\nWHERE (name = N'MySchedule');\n\nEXEC sp_attach_schedule \n    @job_name=N'TransactionLogBackup_Job',\n    @schedule_name=N'MySchedule';\n\nUSE MyDatabaseName; -- Replace with your database name\nGO\n\nBACKUP LOG MyDatabaseName WITH NO_TRUNCATE;