-- Table to store questions\nCREATE TABLE QUESTIONS (\n    QUESTION_ID NUMBER PRIMARY KEY,\n    TEXT VARCHAR2(1000) NOT NULL,\n    ANSWER_TYPE VARCHAR2(50) NOT NULL -- e.g., 'Yes/No', 'Multiple Choice'\n);\n\n-- Table to store possible answers for each question\nCREATE TABLE ANSWERS (\n    ANSWER_ID NUMBER PRIMARY KEY,\n    QUESTION_ID NUMBER,\n    TEXT VARCHAR2(1000) NOT NULL,\n    NEXT_QUESTION_ID NUMBER, -- Points to the next question in the tree\n    CONSTRAINT FK_ANSWER_QUESTION FOREIGN KEY (QUESTION_ID) REFERENCES QUESTIONS(QUESTION_ID),\n    CONSTRAINT FK_NEXT_QUESTION FOREIGN KEY (NEXT_QUESTION_ID) REFERENCES QUESTIONS(QUESTION_ID)\n);\n\n-- Table for initial questions or starting points of different scenarios/pathways\nCREATE TABLE START_QUESTIONS (\n    SCENARIO_ID NUMBER PRIMARY KEY,\n    QUESTION_ID NUMBER NOT NULL,\n    NAME VARCHAR2(1000),\n    CONSTRAINT FK_START_QUESTION FOREIGN KEY (QUESTION_ID) REFERENCES QUESTIONS(QUESTION_ID)\n);\n\n-- Table to store user responses and navigation through the wizard\nCREATE TABLE SESSIONS (\n    SESSION_ID NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,\n    USER_ID VARCHAR2(50) NOT NULL, -- Assuming some form of user ID is needed\n    SCENARIO_ID NUMBER NOT NULL,\n    CURRENT_QUESTION_ID NUMBER NOT NULL,\n    STARTED_DATE TIMESTAMP NOT NULL DEFAULT SYSTIMESTAMP,\n    CONSTRAINT FK_SESSION_SCENARIO FOREIGN KEY (SCENARIO_ID) REFERENCES START_QUESTIONS(SCENARIO_ID),\n    CONSTRAINT FK_CURRENT_QUESTION FOREIGN KEY (CURRENT_QUESTION_ID) REFERENCES QUESTIONS(QUESTION_ID)\n);\n\n-- Table to store individual responses within sessions\nCREATE TABLE RESPONSES (\n    RESPONSE_ID NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,\n    SESSION_ID NUMBER NOT NULL,\n    QUESTION_ID NUMBER NOT NULL,\n    ANSWER_ID NUMBER NOT NULL, -- Could be null if answer is free-form text\n    ANSWER_TEXT VARCHAR2(1000), -- For storing free-form answers\n    CONSTRAINT FK_RESPONSE_SESSION FOREIGN KEY (SESSION_ID) REFERENCES SESSIONS(SESSION_ID),\n    CONSTRAINT FK_RESPONSE_QUESTION FOREIGN KEY (QUESTION_ID) REFERENCES QUESTIONS(QUESTION_ID),\n    CONSTRAINT FK_RESPONSE_ANSWER FOREIGN KEY (ANSWER_ID) REFERENCES ANSWERS(ANSWER_ID)\n);