-- Example reference table creation\n   CREATE TABLE id_mapping (\n     table1_id INT PRIMARY KEY,\n     something_id INT NOT NULL\n   );\n\n   -- Insert mappings\n   INSERT INTO id_mapping (table1_id, something_id) VALUES\n   (1446, 423), (2372, 426), (2402, 428),\n   (2637, 429), (2859, 430), (3659, 433);\n\n   -- Update using the reference table\n   UPDATE table1 t1\n   JOIN id_mapping im ON t1.table1_id = im.table1_id\n   SET t1.something_id = im.something_id\n   WHERE t1.table1_id IN (\n       SELECT table1_id FROM id_mapping\n   );