DECLARE\n     p_cursor refcursor;\n\nBEGIN\n\n    OPEN p_cursor FOR\n\n  SELECT table1.name,\n         table1.timetag AS timetag_table1,\n         table1.status,\n         table2.timetag AS timetag_table2,\n         table2.description\n\n    FROM myFirstTable table1 \n    LEFT OUTER JOIN mySecondTable table2 ON (<data's ids>),   \n    ( \n<query details>\n         );  -- Note: Ensure this parenthesis is correctly placed if needed\n    WHERE\n<more query details>\n\n  RETURN p_cursor;\n\nEND;