SELECT REGEXP_REPLACE(\n    '[lang_de]Hallo![/lang_de][lang_en]Hello![/lang_en]HeyHo[lang_es]Hola![/lang_es]',\n    '(\\[lang_[^]]*\\]).*?(\\/\\[lang_[^]]*\\])|[[:<:]][^[:>:]]*',\n    '\\1\\2'\n) AS cleaned,\n       '[lang_de]Hallo![/lang_de][lang_en]Hello![/lang_en]HeyHo[lang_es]Hola![/lang_es]' AS original\nFROM your_table_name\nHAVING CHAR_LENGTH(cleaned) < CHAR_LENGTH(original);