-- Measurement Type Table\nCREATE TABLE measurement_type (\n    id SERIAL PRIMARY KEY,\n    name TEXT UNIQUE NOT NULL\n);\n\n-- Unit Table with conversion factor to a base unit\nCREATE TABLE unit (\n    id SERIAL PRIMARY KEY,\n    symbol TEXT UNIQUE NOT NULL,  -- e.g., 'K', 'C'\n    long_name TEXT NOT NULL,     -- e.g., 'Kelvin', 'Celsius'\n    measurement_type_id INTEGER REFERENCES measurement_type(id),\n    conversion_factor_to_base NUMERIC NOT NULL,  -- Conversion factor to a base unit of the same type\n    CONSTRAINT unique_measurement_unit UNIQUE (measurement_type_id, symbol)\n);\n\n-- Dimensional Unit Table defining base units for each measurement type\nCREATE TABLE dimensional_unit (\n    id SERIAL PRIMARY KEY,\n    measurement_type_id INTEGER REFERENCES measurement_type(id),\n    dimension TEXT NOT NULL,  -- e.g., 'Thermodynamic temperature', 'Mass/Volume'\n    unit_id INTEGER REFERENCES unit(id),\n    CONSTRAINT unique_dimension_per_type UNIQUE (measurement_type_id, dimension)\n);\n\n-- Measured Value Table\nCREATE TABLE measured_value (\n    id SERIAL PRIMARY KEY,\n    measurement_time TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,\n    value NUMERIC NOT NULL,\n    unit_id INTEGER REFERENCES unit(id) ON DELETE RESTRICT\n);