USE master;\n   GO\n   -- Create a new workload group named 'LowPriority'.\n   CREATE WORKLOAD GROUP LowPriority\n   USING DefaultPool\n   WITH (REQUEST_MAX_CPU_TIME_SEC = 60,\n         REQUEST_MEMORY_GRANT_TIMEOUT_SEC = 30);\n   GO\n\n   -- Create a classifier function to route low priority queries.\n   CREATE FUNCTION dbo.LowPriorityClassifier()\n   RETURNS SYSNAME\n   WITH SCHEMABINDING\n   AS\n   BEGIN\n       DECLARE @workload_group_name AS SYSNAME;\n       IF (APP_NAME() = N'LowPriorityApp')\n           SET @workload_group_name = 'LowPriority';\n       RETURN @workload_group_name;\n   END;\n\n   -- Apply the classifier function to Resource Governor.\n   ALTER RESOURCE GOVERNOR WITH (CLASSIFIER_FUNCTION = dbo.LowPriorityClassifier);\n   ALTER RESOURCE GOVERNOR RECONFIGURE;