CREATE TRIGGER trg_CheckModifiedColumn\nON YourTableName\nAFTER UPDATE\nAS\nBEGIN\n    -- Check if only ColumnOfInterest was modified.\n    IF UPDATE(ColumnOfInterest)\n    BEGIN\n        IF NOT EXISTS (\n            SELECT 1\n            FROM INSERTED i\n                JOIN DELETED d ON i.PrimaryKeyColumn = d.PrimaryKeyColumn\n            WHERE (i.Column1 <> d.Column1 OR \n                   i.Column2 <> d.Column2 OR \n                   -- Add all columns except ColumnOfInterest and PrimaryKeyColumn here.\n                   i.Column150 <> d.Column150)) \n        BEGIN\n            PRINT 'Only ColumnOfInterest was modified.';\n        END;\n    END;\nEND;