-- Common parent table for Fruits and Vegetables\nFoodItems (\n    food_item_id INT PRIMARY KEY AUTO_INCREMENT,\n    item_name VARCHAR(255),\n    item_type ENUM('Fruit', 'Vegetable') NOT NULL\n)\n\nFruits (\n    fruit_id INT PRIMARY KEY,\n    fruitName VARCHAR(255),\n    FOREIGN KEY (fruit_id) REFERENCES FoodItems(food_item_id)\n)\n\nVegetables (\n    vegetable_id INT PRIMARY KEY,\n    vegetableName VARCHAR(255),\n    FOREIGN KEY (vegetable_id) REFERENCES FoodItems(food_item_id)\n)\n\nFavoriteFoods (\n     food_id INT,\n     foodItem_id INT, -- References the food_items table\n     FOREIGN KEY (foodItem_id) REFERENCES FoodItems(food_item_id)\n)