CREATE TABLE KeyFields (\n    KeyID INT IDENTITY(1,1) PRIMARY KEY,\n    KeyField VARCHAR(MAX)\n);\n\nCREATE TABLE Keywords (\n    KeywordID INT IDENTITY(1,1) PRIMARY KEY,\n    Keyword NVARCHAR(255) UNIQUE -- Assuming keywords will not exceed 255 characters\n);\n\nCREATE TABLE KeyFieldKeywords (\n    KeyID INT,\n    KeywordID INT,\n    PRIMARY KEY (KeyID, KeywordID),\n    FOREIGN KEY (KeyID) REFERENCES KeyFields(KeyID),\n    FOREIGN KEY (KeywordID) REFERENCES Keywords(KeywordID)\n);