WITH ImageOrder AS (\n  SELECT 'IMG1' AS IMAGENAME, 1 AS SortOrder UNION ALL\n  SELECT 'IMG2', 2 UNION ALL\n  SELECT 'IMG3', 3 UNION ALL\n  SELECT 'IMG4', 4 UNION ALL\n  SELECT 'IMG5', 5 UNION ALL\n  SELECT 'IMG6', 6\n)\nSELECT i.*\nFROM IMAGES i\nJOIN ImageOrder io ON i.IMAGENAME = io.IMAGENAME\nORDER BY io.SortOrder;