DECLARE\n    CURSOR work_cur IS \n        SELECT assigner_staff_id AS staff_id, active_flag, assign_date,\n               complete_date, mod_date\n        FROM work \n        WHERE assigner_staff_id = '2096';\n    \n    TYPE t_output IS TABLE OF VARCHAR2(32767) INDEX BY PLS_INTEGER;\n    output_lines t_output;\n    line_number NUMBER := 1;\nBEGIN\n    FOR rec IN work_cur LOOP\n        -- Manually format each field and concatenate them into a line\n        output_lines(line_number) := \n            RPAD(rec.staff_id, 8, ' ')   || ' ' || \n            rec.active_flag             || ' ' || \n            TO_CHAR(rec.assign_date,    'YYYY-MM-DD')         || ' ' || \n            TO_CHAR(rec.complete_date,   'YYYY-MM-DD HH24:MI:SS') || ' ' ||\n            TO_CHAR(rec.mod_date,       'YYYY-MM-DD HH24:MI:SS');\n        line_number := line_number + 1;\n    END LOOP;\n\n    -- Loop through output_lines and print each line (for demonstration)\n    FOR i IN 1..output_lines.COUNT LOOP\n        DBMS_OUTPUT.PUT_LINE(output_lines(i));\n    END LOOP;\nEND;\n/