SELECT \n    todo_item.itemId, \n    todo_item.title, \n    todo_itemStatus.statusKey, \n    todo_itemStatus.statusDate \nFROM \n    todo_item\nJOIN \n    todo_project ON todo_item.projectId = todo_project.projectId\nJOIN \n    todo_itemStatus ON todo_itemStatus.itemId = todo_item.itemId AND todo_itemStatus.statusDate = (\n        SELECT MAX(statusDate) \n        FROM todo_itemStatus key1 \n        WHERE key1.itemId = todo_item itemId\n    )\nWHERE \n    todo_project.projectId = 13;