DECLARE @roleName NVARCHAR(128) = 'YourRoleName';\nDECLARE @sqlCommand NVARCHAR(MAX) = N'';\n\n-- Construct GRANT statements for all user tables.\nSELECT @sqlCommand += N'\nGRANT SELECT, REFERENCES, INSERT, UPDATE, DELETE ON [' + \n    SCHEMA_NAME(schema_id) + '].[' + name + '] TO [' + @roleName + '];'\nFROM sys.tables\nWHERE is_ms_shipped = 0; -- Exclude system tables\n\n-- Execute the constructed GRANT commands.\nEXEC sp_executesql @sqlCommand;