-- Create table if it doesn't exist\nCREATE TABLE IF NOT EXISTS your_table (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    column1 VARCHAR(255),\n    column2 VARCHAR(255)\n);\n\n-- Insert data only if the table was just created or is empty\nINSERT IGNORE INTO your_table (column1, column2)\nSELECT 'default value 1', 'default value 2'\nFROM dual\nWHERE NOT EXISTS (SELECT * FROM your_table);