WITH RECURSIVE CategoryPath AS (\n    SELECT id, name, parentID\n    FROM Categories\n    WHERE id = :yourCurrentCategoryId   -- Replace :yourCurrentCategoryId with the actual variable\n\n    UNION ALL\n\n    SELECT c.id, c.name, c.parentID\n    FROM Categories c\n    INNER JOIN CategoryPath cp ON c.id = cp.parentID\n)\nSELECT name\nFROM CategoryPath\nWHERE parentID <> 0     -- Exclude home level (parentID of 0) if needed\nORDER BY id;            -- This order might need adjustment based on your actual tree structure