CREATE PROCEDURE SAFE_DROP_TABLE(@TableName NVARCHAR(256))\nAS\nBEGIN\n    -- Check if the table exists\n    IF EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID('[' + @TableName + ']') AND TYPE IN (N'U'))\n    BEGIN\n        BEGIN TRY\n            -- Attempt to drop the table\n            EXEC('DROP TABLE [' + @TableName + ']');\n            \n            -- If successful, print a success message\n            PRINT 'Dropped table: ' + @TableName;\n        END TRY\n        BEGIN CATCH\n            -- If an error occurs, print an error message\n            PRINT 'Failed to drop table: ' + @TableName + '. Error = ' + CAST(ERROR_NUMBER() AS NVARCHAR(50)) + ': ' + ERROR_MESSAGE();\n        END CATCH\n    ELSE \n    BEGIN\n        -- If the table does not exist, print a no action needed message\n        PRINT 'NO ACTION NEEDED. Table: ' + @TableName + ' does not exist.';\n    END\nEND;