-- Trigger for updating Club's coach reference when Coach's club changes\nCREATE TRIGGER Trg_UpdateCoachClub\nON Coach\nAFTER UPDATE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    -- Update the new club's idCoach to the new coach\n    UPDATE Club\n    SET idCoach = inserted.idCoach\n    FROM Club\n    INNER JOIN inserted i ON Club.idClub = i.idNewClub  -- Assuming you pass new club ID in an additional column for clarity\n\n    -- Remove the old club - coach relationship\n    UPDATE Club\n    SET idCoach = NULL\n    FROM Club\n    INNER JOIN deleted d ON Club.idCoach = d.idCoach\nEND