SELECT \n    u.username,\n    MONTH(e.eventDate) AS month,\n    CASE MONTH(e.eventDate)\n        WHEN 8 THEN 'August'\n        WHEN 9 THEN 'September'\n        WHEN 10 THEN 'October'\n        WHEN 11 THEN 'November'\n        WHEN 12 THEN 'December'\n        WHEN 1 THEN 'January'\n        WHEN 2 THEN 'February'\n        WHEN 3 THEN 'March'\n        WHEN 4 THEN 'April'\n        WHEN 5 THEN 'May'\n    END AS month_name,\n    SUM(CASE WHEN DAYOFWEEK(e.eventDate) BETWEEN 2 AND 6 THEN 1 ELSE 0 END) AS weekdays_count,\n    SUM(CASE WHEN DAYOFWEEK(e.eventDate) IN (1, 7) THEN 1 ELSE 0 END) AS weekends_count\nFROM \n    users u\nJOIN \n    eventcal e ON u.username = e.primary\nWHERE \n    (MONTH(e.eventDate) BETWEEN 8 AND 12 OR MONTH(e.eventDate) BETWEEN 1 AND 5)\nGROUP BY \n    u.username, month, month_name\nORDER BY \n    u.username, MONTH(e.eventDate);