DECLARE @ID INT;\nDECLARE @BatchSize INT = 100; -- Process 100 rows at a time\n\nDECLARE RowCursor CURSOR FOR \nSELECT ID FROM LargeTable WHERE Processed = 0;\n\nOPEN RowCursor;\nFETCH NEXT FROM RowCursor INTO @ID;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Process each batch of rows here\n    UPDATE LargeTable SET Processed = 1 WHERE ID = @ID;\n\n    FETCH NEXT FROM RowCursor INTO @ID;\nEND\n\nCLOSE RowCursor;\nDEALLOCATE RowCursor;