-- Insert data into Country Table\nINSERT INTO Country (CountryName)\nVALUES ('Afghanistan'), ('Albania'), ('Algeria') -- Add all other countries;\n\n-- Populate SpeciesFoundInCountry table dynamically\nSET @query = '';\n\nSELECT GROUP_CONCAT(\n    CONCAT('INSERT INTO SpeciesFoundInCountry (CountryID, SpeciesID) \n            SELECT T2.CountryID, T1.ID FROM species AS T1 \n            INNER JOIN Country AS T2 ON T2.CountryName = ''', COLUMN_NAME, ''' \n            WHERE T1.`', COLUMN_NAME, '`= 1;') SEPARATOR ''\n) INTO @query\nFROM INFORMATION_SCHEMA.COLUMNS \nWHERE table_name = 'species' \nAND column_name NOT IN ('ID', 'Species');\n\n-- Prepare and execute the dynamic query\nPREPARE stmt FROM @query;\nEXECUTE stmt;\nDEALLOCATE PREPARE stmt;