DELIMITER //\n\n   CREATE PROCEDURE InsertNewRecord(\n       IN p_id INT,\n       IN p_data VARCHAR(255)\n   )\n   BEGIN\n       IF p_id >= x THEN\n           INSERT INTO your_table (id, data) VALUES (p_id, p_data);\n       ELSE\n           SIGNAL SQLSTATE '45000'\n           SET MESSAGE_TEXT = 'You cannot insert into this range of IDs.';\n       END IF;\n   END //\n\n   DELIMITER ;