SELECT \n    address,\n    MAX(CASE WHEN forename = 'John' THEN surname END) AS John,\n    MAX(CASE WHEN forename = 'Lucy' THEN surname END) AS Lucy,\n    MAX(CASE WHEN forename = 'Jenny' THEN surname END) AS Jenny,\n    MAX(CASE WHEN forename = 'Steve' THEN surname END) AS Steve,\n    MAX(CASE WHEN forename = 'Richard' THEN surname END) AS Richard\nFROM \n    your_table_name\nGROUP BY \n    address;