USE [Database]\nGO\n\nALTER TRIGGER [dbo].[CopyNames] ON [dbo].[Patients]\nAFTER INSERT, UPDATE\nAS \nBEGIN\n    SET NOCOUNT ON;\n\n    -- Update FirstName if First was updated/inserted\n    IF UPDATE([First])\n    BEGIN\n        UPDATE p\n        SET p.FirstName = i.First\n        FROM [dbo].[Patients] p\n        INNER JOIN inserted i ON p.PatientID = i.PatientID;\n    END\n\n    -- Update MiddleName if Middle was updated/inserted\n    IF UPDATE([Middle])\n    BEGIN\n        UPDATE p\n        SET p.MiddleName = i.Middle\n        FROM [dbo].[Patients] p\n        INNER JOIN inserted i ON p.PatientID = i.PatientID;\n    END\n\n    -- Update LastName if Last was updated/inserted\n    IF UPDATE([Last])\n    BEGIN\n        UPDATE p\n        SET p.LastName = i.Last\n        FROM [dbo].[Patients] p\n        INNER JOIN inserted i ON p.PatientID = i.PatientID;\n    END\nEND\nGO