DECLARE\n       active_sessions NUMBER;\n   BEGIN\n       -- Count the current number of active sessions\n       SELECT COUNT(*) INTO active_sessions FROM v$session WHERE username IS NOT NULL;\n\n       -- Insert the count and timestamp into the logging table\n       INSERT INTO session_counts (count_time, session_count)\n       VALUES (SYSTIMESTAMP, active_sessions);\n\n       COMMIT;\n   END;