DECLARE @jobName NVARCHAR(256) = N'YourJobNameHere';\n\nBEGIN TRY\n    -- Check if the job exists before attempting to delete it\n    IF EXISTS (SELECT 1 FROM msdb.dbo.sysjobs WHERE name = @jobName)\n    BEGIN\n        EXEC msdb.dbo.sp_delete_job @job_name = @jobName;\n        PRINT 'SQL Server Job [' + @jobName + '] deleted successfully.';\n    END\n    ELSE\n    BEGIN\n        PRINT 'SQL Server Job [' + @jobName + '] does not exist.';\n    END\nEND TRY\nBEGIN CATCH\n    PRINT 'Error deleting the job: ' + ERROR_MESSAGE();\nEND CATCH;