CREATE TRIGGER [OnItem_Updated]\n        ON [Item]\n       AFTER UPDATE\n    AS \n    BEGIN\n        RETURN; -- This will prevent the trigger from executing any logic\n    END;\n\n    ALTER TABLE [Item] DISABLE TRIGGER [OnItem_Updated];\n    \n    -- Once testing is done, update the trigger body and enable it\n    ALTER TRIGGER [OnItem_Updated]\n    ON [Item]\n    AFTER UPDATE\n    AS \n    BEGIN\n        SET NOCOUNT ON;\n    \n        IF update(State)\n        BEGIN\n            /* Trigger logic */\n        END\n    END;\n\n    ALTER TABLE [Item] ENABLE TRIGGER [OnItem_Updated];