-- Starting SNAPSHOT isolation level\nSET TRANSACTION ISOLATION LEVEL SNAPSHOT;\nGO\n\nBEGIN TRANSACTION;\n\n    -- Assume this is an existing row in 'YourTable'\n    UPDATE YourTable \n    SET Column1 = 'NewValue' \n    WHERE ID = 1;\n    \n    -- You can see your own changes even though no commit is done\n    SELECT * FROM YourTable WHERE ID = 1; -- This shows the new value\n\nCOMMIT TRANSACTION;\n\n-- This transaction will not see the change from above after its start\nBEGIN TRANSACTION;\n    SELECT * FROM YourTable WHERE ID = 1; -- This still shows the old value, before 'NewValue' was set\nCOMMIT TRANSACTION;