SELECT \n    CLIENT.client_id,\n    CLIENT.client_name,\n    OFFICE.office_name AS case_office_name,\n    MAX(OFFICE_MIS.office_mis_name) AS referral_office_name\nFROM \n    CLIENT\nLEFT OUTER JOIN \n    OFFICE ON CLIENT.CASE_OFFICE = OFFICE.TABLE_CODE\nLEFT OUTER JOIN \n    OFFICE_MIS ON CLIENT.REFERRAL_OFFICE = OFFICE_MIS.TABLE_CODE\nGROUP BY \n    CLIENT.client_id, CLIENT.client_name, OFFICE.office_name;