CREATE TRIGGER update_presentations\nON Presentations\nAFTER UPDATE\nAS\nBEGIN\n    IF trigger_nestlevel() < 2\n        BEGIN\n            UPDATE Events\n            SET Date = inserted.Date,\n                Location = inserted.Location\n            FROM Presenter JOIN inserted ON Presentations.EventID = Events.ID;\n        END;\nEND\n\nCREATE TRIGGER update_events\nON Events\nAFTER UPDATE\nAS\nBEGIN\n    IF trigger_nestlevel() < 2\n        BEGIN\n            UPDATE Presentations\n            SET Date = inserted.Date,\n                Location = inserted.Location\n            FROM Events JOIN inserted ON Events.PresentationID = Presentations.ID;\n        END;\nEND