TABLE Node (\n    NodeId INT PRIMARY KEY,\n    ...\n)\n\nTABLE ContentBase (\n    ContentId INT PRIMARY KEY,\n    ContentType VARCHAR(255)\n)\n\nTABLE TextContent (\n    ContentId INT PRIMARY KEY REFERENCES ContentBase(ContentId),\n    TextData TEXT\n)\n\nTABLE ImageContent (\n    ContentId INT PRIMARY KEY REFERENCES ContentBase(ContentId),\n    ImageData BLOB\n)\n\nTABLE NodeContentRelationship (\n    NodeId INT REFERENCES Node(NodeId),\n    ContentType VARCHAR(255),  -- Should match the ContentType in ContentBase\n    ContentId INT\n)