-- Example T-SQL to generate bcp commands\nDECLARE @SQL NVARCHAR(MAX);\nDECLARE @FilePath VARCHAR(255);\n\nSET @FilePath = 'C:\path\to\export_$(Owner).csv';\n\nSELECT @SQL = N'EXEC master..xp_cmdshell ''bcp "SELECT * FROM YourDatabase.dbo.YourTable WHERE OwnershipCriteria = ''' + Owner + '''" queryout "' + REPLACE(@FilePath, '$(Owner)', Owner) + '" -w -T -S YourServerName'';'\nFROM (\n    SELECT DISTINCT OwnershipCriteria AS Owner\n    FROM YourDatabase.dbo.YourTable\n) o;\n\nEXEC sp_executesql @SQL;