-- Table for the main entity, let's call it `items`\nCREATE TABLE items (\n    item_id INT AUTO_INCREMENT PRIMARY KEY,\n    -- other fields related to the item can go here\n    name VARCHAR(255)\n);\n\n-- Table for the words associated with each item\nCREATE TABLE item_words (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    item_id INT NOT NULL,\n    word VARCHAR(100),  -- Adjust length as necessary\n    FOREIGN KEY (item_id) REFERENCES items(item_id) ON DELETE CASCADE\n);