WITH CleanData AS (\n    SELECT \n        myColumn,\n        REPLACE(myColumn, SUBSTRING(myColumn, PATINDEX('%[^0-9]%', myColumn), 1), '') AS cleanedColumn\n    FROM \n        myTable\n    WHERE \n        PATINDEX('%[^0-9]%', myColumn) > 0\n\n    UNION ALL\n\n    SELECT \n        t.myColumn,\n        REPLACE(cd.cleanedColumn, SUBSTRING(cd.cleanedColumn, PATINDEX('%[^0-9]%', cd.cleanedColumn), 1), '') AS cleanedColumn\n    FROM \n        CleanData cd\n    INNER JOIN \n        myTable t ON t.myColumn = cd.myColumn\n    WHERE \n        PATINDEX('%[^0-9]%', cd.cleanedColumn) > 0\n)\nUPDATE myTable\nSET myColumn = cleanedColumn\nFROM CleanData\nWHERE myTable.myColumn = CleanData.myColumn\nOPTION (MAXRECURSION 0);