CREATE Procedure StoredProc2 \n    @AmountOfNames [int] OUTPUT\nAS\nBEGIN\n    -- Declare a temporary table with the same structure as the result set from StoredProc1\n    DECLARE @Users TABLE (UserID int, Name varchar(50), DateOfBirth datetime);\n    \n    -- Insert the results of StoredProc1 into the temporary table\n    INSERT INTO @Users (UserID, Name, DateOfBirth)\n    EXEC StoredProc1 @Name = 'Irwin';\n    \n    -- Count the rows in the temporary table and set @AmountOfNames\n    SELECT @AmountOfNames = COUNT(*) FROM @Users;\nEND