CREATE PROCEDURE GetPivotedMonthlyData\nAS\nBEGIN\n    SELECT \n        YEAR(DateColumn) AS [Year],\n        SUM(CASE WHEN MONTH(DateColumn) = 1 THEN ValueColumn ELSE 0 END) AS [Jan],\n        SUM(CASE WHEN MONTH(DateColumn) = 2 THEN ValueColumn ELSE 0 END) AS [Feb],\n        SUM(CASE WHEN MONTH(DateColumn) = 3 THEN ValueColumn ELSE 0 END) AS [Mar],\n        SUM(CASE WHEN MONTH(DateColumn) = 4 THEN ValueColumn ELSE 0 END) AS [Apr],\n        SUM(CASE WHEN MONTH(DateColumn) = 5 THEN ValueColumn ELSE 0 END) AS [May],\n        SUM(CASE WHEN MONTH(DateColumn) = 6 THEN ValueColumn ELSE 0 END) AS [Jun],\n        SUM(CASE WHEN MONTH(DateColumn) = 7 THEN ValueColumn ELSE 0 END) AS [Jul],\n        SUM(CASE WHEN MONTH(DateColumn) = 8 THEN ValueColumn ELSE 0 END) AS [Aug],\n        SUM(CASE WHEN MONTH(DateColumn) = 9 THEN ValueColumn ELSE 0 END) AS [Sep],\n        SUM(CASE WHEN MONTH(DateColumn) = 10 THEN ValueColumn ELSE 0 END) AS [Oct],\n        SUM(CASE WHEN MONTH(DateColumn) = 11 THEN ValueColumn ELSE 0 END) AS [Nov],\n        SUM(CASE WHEN MONTH(DateColumn) = 12 THEN ValueColumn ELSE 0 END) AS [Dec],\n        SUM(ValueColumn) AS [Total]\n    FROM \n        YourTableName\n    GROUP BY \n        YEAR(DateColumn)\n    ORDER BY \n        [Year];\nEND;