DECLARE\n  service_     UTL_DBWS.SERVICE;\n  port_        UTL_DBWS.PORT;\n  operation_   UTL_DBWS.OPERATION;\n  request_     UTL_DBWS.ELEMENT;\n  response_    UTL_DBWS.ELEMENT;\n  input_msg    SYS.XMLTYPE := SYS.XMLTYPE('<MyRequest><param1>value</param1></MyRequest>');\n  output_msg   SYS.XMLTYPE;\nBEGIN\n  -- Create service\n  service_ := UTL_DBWS.CREATE_SERVICE('http://example.com/service?wsdl', 'Service');\n  \n  -- Get port and operation\n  port_     := UTL_DBWS.GET_PORT(service_, 'Port');\n  operation_:= UTL_DBWS.GET_OPERATION(port_, 'OperationName');\n  \n  -- Prepare request\n  request_  := UTL_DBWS.CREATE_ELEMENT('http://example.com/schemas', 'MyRequest', input_msg);\n  \n  -- Invoke the operation\n  response_ := UTL_DBWS.INVOKE(operation_, request_);\n  \n  -- Extract output message\n  output_msg := UTL_DBWS.GET_CDATA(response_);\n  \n  DBMS_OUTPUT.PUT_LINE('Response: ' || output_msg.getStringVal());\nEND;