CREATE PROCEDURE GetDataFromDynamicTable\n    @TableName NVARCHAR(128),\n    @Filter NVARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @SQLCommand NVARCHAR(MAX)\n\n    -- Construct the SQL command with dynamic table name and filter\n    SET @SQLCommand = N'SELECT * FROM ' + QUOTENAME(@TableName) + N' WHERE ' + @Filter\n\n    -- Execute the constructed SQL command\n    EXEC sp_executesql @SQLCommand\nEND