SELECT \n    fk.name AS ForeignKeyName,\n    t1.name AS TableName,\n    c1.name AS ConstraintColumnName,\n    OBJECT_NAME(fk.referenced_object_id) AS ReferencedTableName,\n    COL_NAME(fc.parent_object_id, fc.parent_column_id) AS ReferencedColumnName\nFROM \n    sys.foreign_keys AS fk\nJOIN \n    sys.tables t1 ON fk.parent_object_id = t1.object_id\nJOIN \n    sys.syscolumns c1 ON t1.id = c1.id AND c1.colid IN (\n        SELECT parent_column_id FROM sys.foreign_key_columns WHERE constraint_object_id = fk.constraint_object_id)\nJOIN \n    sys.foreign_key_columns AS fc ON fk.OBJECT_ID = fc.constraint_object_id\nWHERE \n    t1.name='your_table_name';