CREATE TABLE POP (id INT PRIMARY KEY AUTO_INCREMENT, population VARCHAR(255) NOT NULL UNIQUE);\nCREATE TABLE IND (id INT PRIMARY KEY AUTO_INCREMENT, fk_pop INT, name VARCHAR(255), FOREIGN KEY (fk_pop) REFERENCES POP(id));\nCREATE TABLE MARKER (id INT PRIMARY KEY AUTO_INCREMENT, rsid VARCHAR(255) NOT NULL UNIQUE);\nCREATE TABLE GENOTYPE (id INT PRIMARY KEY AUTO_INCREMENT, fk_ind INT, fk_rsid INT, call VARCHAR(255), FOREIGN KEY (fk_ind) REFERENCES IND(id), FOREIGN KEY (fk_rsid) REFERENCES MARKER(id));