SELECT \n    banners.id,\n    banners.title,\n    banners.text,\n    GROUP_CONCAT(languages.code ORDER BY languages.code SEPARATOR ', ') AS languages\nFROM \n    banners\nJOIN \n    content_id_country ON content_id_country.content_id = banners.id AND content_id_country.controller = 'banners'\nJOIN \n    languages ON languages.id = content_id_country.language_id  -- Assuming there's a language_id in content_id_country\nGROUP BY \n    banners.id, banners.title, banners.text;