-- Example of creating a check constraint for specific node types\nALTER TABLE Nodes \nADD CONSTRAINT chk_LinkNode_Url CHECK (\n    (NodeType = 'LinkNode' AND Url IS NOT NULL) OR\n    NodeType != 'LinkNode'\n);\n\nALTER TABLE Nodes \nADD CONSTRAINT chk_HtmlPageNode_ContentHtml CHECK (\n    (NodeType = 'HtmlPageNode' AND ContentHtml IS NOT NULL) OR\n    NodeType != 'HtmlPageNode'\n);\n\nALTER TABLE Nodes \nADD CONSTRAINT chk_CodePageNode_ContentCode CHECK (\n    (NodeType = 'CodePageNode' AND ContentCode IS NOT NULL) OR\n    NodeType != 'CodePageNode'\n);